        let productjson = {}
        productjson.id = cookieProductId
        productjson.qty = quantityNum.qtyNum
        cookieProduct.push(productjson)
      }
      // 如果被改變後的event數量>0，加入在cookie 中
      for (let i in cookieProduct) {
        if (cookieProduct[i].qty > 0) {
