import './style/userAuctionOver.scss'
import Logoheader from './components/Logoheader'
import Breadcrumb from '../user/components/UserBreadcrumb'
import { withRouter, Link, NavLink } from 'react-router-dom'
import React, { useEffect, useState } from 'react'
import PageNumber from './components/pageNumber'
// SweetAlert
import swal from 'sweetalert'
import {
  Container,
  Row,
  Button,
  Collapse,
} from 'react-bootstrap'
import {
  IoIosArrowBack,
  IoIosArrowForward,
  IoIosArrowRoundDown,
  IoIosSearch,
  IoIosHeart,
  IoMdAdd,
  IoMdRemove,
} from 'react-icons/io'

function UserAuctionOver(props) {
  // const userid = props.match.params.userid
  //頁碼
  //該頁頁碼
  const [pages, setPages] = useState([])
  //頁碼資料
  const [pagesinfo, setPagesInfo] = useState('')
  //顯示的頁碼
  const [showPages, setShowPages] = useState([
    1, 2, 3, 4, 5,
  ])

  const [userid, setUserId] = useState('')
  const [memAucOrderData, setMemAucOrderData] = useState([])
  const [orderStatus, setOrderStatus] = useState([])

  async function getMemAucDetailFromServer(userid) {
    // 開啟載入指示
    // setDataLoading(true)
    // 連接的伺服器資料網址
    const url =
      `http://localhost:6005/auctoin/auc-order` + `?userId=${userid}`+ `&pages=${pages}`
    // `&pages=${pages}`

    // 注意header資料格式要設定，伺服器才知道是json格式
    const request = new Request(url, {
      method: 'GET',
      headers: new Headers({
        Accept: 'application/json',
        'Content-Type': 'appliaction/json',
      }),
    })

    const response = await fetch(request)
    const data = await response.json()
    // //設定資料
    setMemAucOrderData(data.rows)
    // //設定頁數資料
    setPagesInfo(data)

    console.log(data)
    //設定頁碼
    if (data.totalPages < 6) {
      let pagelength = []
      for (let i = 0; i < data.totalPages; i++) {
        pagelength.push(i + 1)
      }
      setShowPages(pagelength)
    }
    console.log(data.rows.length)
    let a = [] 
    for (let i = 0; i < data.rows.length; i++) {
      if (data.rows[i].orderStatus === 0) {
        a.push('待出貨')
        console.log(orderStatus)
      }
      if (data.rows[i].orderStatus === 1) {
        a.push('已出貨')
        console.log(orderStatus)
      }
      if (data.rows[i].orderStatus === 2) {
        a.push('已取消')
        console.log(orderStatus)
      }
    }
    setOrderStatus(a)
  }

  async function logoutToSever() {
    // 連接的伺服器資料網址
    const url = 'http://localhost:6005/users/logout'

    // 注意資料格式要設定，伺服器才知道是json格式
    const request = new Request(url, {
      method: 'GET',
      headers: new Headers({
        Accept: 'application/json',
        'Content-Type': 'application/json',
      }),
    })

    // 要等驗証過，再設定資料(簡單的直接設定)

    swal({
      text: '登出成功！',
      icon: 'success',
      button: false,
      timer: 3000,
    })

    const response = await fetch(request)
    // const data = await response.json()
  }

  //驗證身分
