import { React, useState, useEffect } from 'react'
import { useForm } from 'react-hook-form'
import {
  countries,
  townships,
  data,
} from './data/townships'
import Cookies from 'universal-cookie'

import fakedata from './data/fakedata'
import Basket2 from './demo/Basket2'

import { ReactComponent as Logo } from '../../pics/logo-bk.svg'

// components
import CartFormShip from './components/CartFormShip'
import CartFormPay from './components/CartFormPay'
import CartFormFinish from './components/CartFormFinish'

// @material-ui
import FormControlLabel from '@material-ui/core/FormControlLabel'
import Checkbox from '@material-ui/core/Checkbox'

// styles
import '../../bootstrap/css/bootstrap.css'
import './styles/cart-shipping.scss'
import './styles/cart-payment.scss'

// imgae
import img1 from './img/1.png'
import img2 from './img/2.png'

// icon
import { FaLock } from 'react-icons/fa'
import { RiArrowRightSLine } from 'react-icons/ri'

function CartForm() {
  // react hook form
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({ mode: 'all' })
  const onSubmit = (data) => console.log(data)

  // shipping info
  //   const [status, setStatus] = useState(0)
  //   const radioHandler = (status) => {
  //     setStatus(status)
  //   }
  const [country, setCountry] = useState(-1)
  const [township, setTownship] = useState(-1)

  // checkbox
  const [state, setState] = useState({
    checkedA: true,
    checkedB: true,
  })
  const handleChange = (event) => {
    setState({
      ...state,
      [event.target.name]: event.target.checked,
    })
  }

  //   const [orderInfo, setOrderInfo] = useState({})
  //   const updateOrderInfo = (key, value) => {
  //     orderInfo[key] = value
  //     setOrderInfo(orderInfo)
  //     console.log(orderInfo)
  //   }
  // formstep
  const [formStep, setFormStep] = useState(0)
  const completeFormStep = () => {
    console.log(setFormStep)
    setWholeAddress(
      `${countries[country]}${townships[country][township]}${address}`
    )
    setFormStep((cur) => cur + 1)
  }
  // formbutton
  const renderButton = () => {
    if (formStep > 1) {
      return undefined
    } else if (formStep === 1) {
      return (
        <div className="my-4">
          <button
            type="button"
            disabled={
              !username ||
              country == -1 ||
              township == -1 ||
              !address ||
              !mobile
            }
            onClick={postOrderToSever}
            key="1"
            // onClick={completeFormStep}
            className="c-checkoutbtn2"
          >
            確認付款
          </button>
        </div>
      )
    } else {
      return (
        <div className="my-4">
          <button
            disabled={
              !username ||
              country == -1 ||
              township == -1 ||
              !address ||
              !mobile
            }
            onClick={completeFormStep}
            type="button"
            key="2"
            className="c-checkoutbtn2"
          >
            繼續結帳
          </button>
        </div>
      )
    }
  }
  // 回前一頁
  const goToPreviousStep = () => {
    setFormStep((cur) => cur - 1)
  }

  const { products } = fakedata
  const [cartItems, setCartItems] = useState([])
  const cookies = new Cookies()

  useEffect(() => {
    let tempArr = []
